  `USER_ID_NUM` varchar(50) DEFAULT NULL,
  `USER_REAL_NAME` varchar(50) DEFAULT '' COMMENT 'User Real Name',
  `USER_PASSWORD` varchar(50) DEFAULT NULL,
  `USER_MOBILE` varchar(50) DEFAULT '' COMMENT 'Mobile No',
  `USER_EMAIL` varchar(50) DEFAULT NULL COMMENT '邮箱',
  `SHOP_INTEGRAL` varchar(50) DEFAULT NULL COMMENT '购物积分',
  `ACTIVE_INTEGRAL` varchar(50) DEFAULT NULL COMMENT '激活积分',
  `REGISTER_INTEGRAL` varchar(50) DEFAULT NULL COMMENT '注册积分',
  `FUND` varchar(50) DEFAULT NULL COMMENT '基金',
  `BONUS` varchar(50) DEFAULT NULL COMMENT '可提现的钱',
  `OTHER_BUY_INTEGRAL` varchar(50) DEFAULT NULL COMMENT '代购积分',
  `STOCK` varchar(50) DEFAULT NULL,
  `ID_TYPE` varchar(50) DEFAULT NULL COMMENT '证件类型',
  `INVESTMENT` varchar(50) DEFAULT NULL COMMENT '投资额',
  `COUNTRY` varchar(50) DEFAULT NULL COMMENT '国家',
  `USER_PARENT_NAME` varchar(50) DEFAULT '' COMMENT 'User Parent Name',
  `ACTIVE` tinyint(4) DEFAULT '0' COMMENT 'Active',
  `CREATE_USER_ID` varchar(100) DEFAULT '' COMMENT 'Create User ID',
  `CREATE_USER_NAME` varchar(100) DEFAULT '' COMMENT 'Create User Name',
  `CREATE_TIME` timestamp NULL DEFAULT NULL COMMENT 'Create Time',
  `UPDATE_USER_ID` varchar(100) DEFAULT '' COMMENT 'Update User ID',
  `UPDATE_USER_NAME` varchar(100) DEFAULT '' COMMENT 'Update User Name',
  `UPDATE_TIME` timestamp NULL DEFAULT NULL COMMENT 'Update Time',
  `VERSION` decimal(10,0) DEFAULT NULL COMMENT 'Version',
  PRIMARY KEY (`ID`),
  KEY `idx_wx_id` (`WX_ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

